239=>['campaign'=>1,'profession'=>6,'attribute'=>11,'elite'=>true,'split'=>true,'pve'=>['type'=>26,'upkeep'=>0,'energy'=>15,'activation'=>1,'recharge'=>20,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Abwehr gegen Schaden','en'=>'Ward Against Harm'],'desc'=>['de'=>'Errichtet an dieser Position eine Abwehr gegen Schaden. 5..15 Sekunden lang erhalten in diesem Bereich befindliche Verbündete, die keine Geister sind, +1..3 Lebenspunktregeneration. Gruppenmitglieder erhalten außerdem +12..24 Rüstung und doppelt so viel Rüstung gegen Elementarschaden.','en'=>'Create a Ward Against Harm at this location. For 5..15 seconds, non-spirit allies in this area have +1..3 health regeneration, +12..24 armor, and an additional +12..24 armor against elemental damage. This spell is disabled for 20 seconds.'],'concise'=>['de'=>'','en'=>'(5...15 seconds.) Allies in this ward have +1...3 Health regeneration, +12...24 armor, and +12...24 additional armor against elemental damage. <i>Spirits are unaffected. This spell is disabled for 20 seconds.</i>']],'pvp'=>['type'=>26,'upkeep'=>0,'energy'=>15,'activation'=>1,'recharge'=>30,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Abwehr gegen Schaden (PvP)','en'=>'Ward Against Harm (PvP)'],'desc'=>['de'=>'Errichtet an dieser Position eine Abwehr gegen Schaden. 5..15 Sekunden lang erhalten in diesem Bereich befindliche Verbündete, die keine Geister sind, +1..3 Lebenspunktregeneration. Gruppenmitglieder erhalten außerdem +12..24 Rüstung und doppelt so viel Rüstung gegen Elementarschaden.','en'=>'Create a Ward Against Harm at this location. For 5..15 seconds, non-spirit allies in this area have +1..3 health regeneration, +12..24 armor, and an additional +12..24 armor against elemental damage. This spell is disabled for 30 seconds.'],'concise'=>['de'=>'','en'=>'(5...15 seconds.) Allies in this ward have +1...3 Health regeneration, +12...24 armor, and +12...24 additional armor against elemental damage. <i>Spirits are unaffected. This spell is disabled for 30 seconds.</i>']]],
