846=>['campaign'=>3,'profession'=>6,'attribute'=>11,'elite'=>false,'split'=>false,'pve'=>['type'=>22,'upkeep'=>0,'energy'=>5,'activation'=>1,'recharge'=>8,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Dampf','en'=>'Steam'],'desc'=>['de'=>'Der Gegner erleidet 20..60 Punkte Kälteschaden. Brennt der Gegner, wird er 5..10 Sekunden lang durch den Dampf geblendet.','en'=>' Target foe is struck for 20..60 cold damage. If target foe is on fire, Steam Blinds that foe for 5..10 seconds.'],'concise'=>['de'=>'','en'=>'Deals 20...60 cold damage. Inflicts Blindness condition (5...10 seconds) if target foe is Burning.']],'pvp'=>null],
