848=>['campaign'=>2,'profession'=>3,'attribute'=>15,'elite'=>false,'split'=>false,'pve'=>['type'=>23,'upkeep'=>0,'energy'=>10,'activation'=>0.25,'recharge'=>10,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Verhexung umkehren','en'=>'Reverse Hex'],'desc'=>['de'=>'Entfernt eine Verhexung vom Verbündeten (bzw. Euch selbst). 5..10 Sekunden lang wird der nächste Schaden, den der Verbündete (bzw. Ihr selbst) erleiden würde, um 5..50 Punkte verringert.','en'=>'Remove one hex from target ally. If a hex was removed in this way, for 5..10 seconds, the next time target ally would take damage, that damage is reduced by 5..50.'],'concise'=>['de'=>'','en'=>'(5...10 seconds.) Removes one hex from target ally. The next damage this ally takes is reduced by 5...50. <i>No effect unless this ally is hexed.</i>']],'pvp'=>null],
849=>['campaign'=>2,'profession'=>1,'attribute'=>18,'elite'=>false,'split'=>false,'pve'=>['type'=>4,'upkeep'=>0,'energy'=>0,'activation'=>0,'recharge'=>0,'adrenaline'=>5,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Zerfetzender Hieb','en'=>'Lacerating Chop'],'desc'=>['de'=>'Wenn Zerfetzender Hieb erfolgreich ist, fügt Ihr +5..20 Punkte Schaden zu. Wenn Ihr einen Feind am Boden trefft, leidet er 5..20 Sekunden lang unter einer Blutung.','en'=>'If Lacerating Chop hits, you deal  +5..20 damage. If it strikes a knocked down foe your target suffers from Bleeding for 5..20 seconds.'],'concise'=>['de'=>'','en'=>'Deals +5...20 damage. Inflicts Bleeding condition (5...20 seconds) if target foe is knocked-down.']],'pvp'=>null],
850=>['campaign'=>2,'profession'=>1,'attribute'=>19,'elite'=>false,'split'=>false,'pve'=>['type'=>8,'upkeep'=>0,'energy'=>0,'activation'=>0,'recharge'=>0,'adrenaline'=>6,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Erbitterter Schlag','en'=>'Fierce Blow'],'desc'=>['de'=>'Wenn Erbitterter Schlag trifft, fügt Ihr +5..20 Punkte zu. Wenn der Gegner an Schwäche leidet, erleidet er außerdem 1..8 Sekunden lang eine Tiefe Wunde.','en'=>'If Fierce Blow hits, you strike for +5..20 damage. If target foe was  suffering from Weakness, that foe also suffers from a Deep Wound for 1..8 second[s].'],'concise'=>['de'=>'','en'=>'Deals +5...20 damage. Inflicts Deep Wound (1...8 seconds) if target foe is Weakened.']],'pvp'=>null],
