852=>['campaign'=>2,'profession'=>2,'attribute'=>25,'elite'=>false,'split'=>false,'pve'=>['type'=>2,'upkeep'=>0,'energy'=>10,'activation'=>0,'recharge'=>5,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Splitterschuss','en'=>'Splinter Shot'],'desc'=>['de'=>'Wenn Splitterschuss trifft, fügt Ihr dem Gegner +3..15 Punkte Schaden zu. Wenn Splitterschuss geblockt wird, erleiden alle Feinde um Euren Gegner 5..65 Punkte Schaden.','en'=>'If Splinter Shot hits, you deal +3..15 damage. If Splinter Shot is blocked, all foes adjacent to your target take 5..65 damage.'],'concise'=>['de'=>'','en'=>'Deals +3...15 damage. Deals 5...65 damage to adjacent foes if blocked.']],'pvp'=>null],
