1137=>['campaign'=>2,'profession'=>1,'attribute'=>19,'elite'=>false,'split'=>false,'pve'=>['type'=>8,'upkeep'=>0,'energy'=>0,'activation'=>0,'recharge'=>0,'adrenaline'=>6,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Yetischlag','en'=>'Yeti Smash'],'desc'=>['de'=>'Alles Adrenalin geht verloren. Greift alle umstehenden Feinde an. Wenn dieser Angriff einen Gegner trifft, der unter einem Zustand leidet, wird dieser zu Boden geworfen. (Bei einer Hammerbeherrschung von 4 oder weniger besteht eine 50%ige Wahrscheinlichkeit für einen Misserfolg.)','en'=>'Lose all adrenaline. Attack all adjacent foes. If this attack strikes a foe suffering from a condition, that foe is knocked down. (50% failure chance with Hammer Mastery 4 or less.)'],'concise'=>['de'=>'','en'=>'Attack all adjacent foes. Knocks down foes suffering from a condition. <i>You lose all adrenaline. 50% failure chance unless Hammer Mastery is 5 or more.</i>']],'pvp'=>null],
1141=>['campaign'=>2,'profession'=>1,'attribute'=>17,'elite'=>false,'split'=>false,'pve'=>['type'=>20,'upkeep'=>0,'energy'=>5,'activation'=>0,'recharge'=>15,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'"Ihr werdet sterben!"','en'=>'"You Will Die!"'],'desc'=>['de'=>'Wenn die Lebenspunkte des Gegners unter 50% liegen, erhaltet Ihr 1..3 Adrenalinstöße.','en'=>'If target foe is below 50% Health, you gain 1..3 strike[s] of adrenaline.'],'concise'=>['de'=>'','en'=>'You gain 1...3 strikes of adrenaline. <i>No effect unless target foe is under 50% Health.</i>']],'pvp'=>null],
1142=>['campaign'=>2,'profession'=>1,'attribute'=>21,'elite'=>true,'split'=>false,'pve'=>['type'=>29,'upkeep'=>0,'energy'=>0,'activation'=>0,'recharge'=>2,'adrenaline'=>1,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Günstige Abwehr','en'=>'Auspicious Parry'],'desc'=>['de'=>'8 Sekunden lang wird der nächste Angriff gegen Euch geblockt und diese Haltung endet. Ihr erhaltet 1..4 Adrenalinstöße, wenn diese Haltung endet.','en'=>'For 8 seconds, the next attack against you is blocked. You gain 1..4 strike[s] of adrenaline when this stance ends.'],'concise'=>['de'=>'','en'=>'(8 seconds.) Blocks one attack. End effect: you gain 1...4 strikes of adrenaline.']],'pvp'=>null],
