1202=>['campaign'=>2,'profession'=>2,'attribute'=>22,'elite'=>true,'split'=>true,'pve'=>['type'=>11,'upkeep'=>0,'energy'=>5,'activation'=>0,'recharge'=>5,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Erzürnter Ausfall','en'=>'Enraged Lunge'],'desc'=>['de'=>'Euer Tiergefährte greift mit einem Erzürnten Ausfall an, der dem Gegner 5..20 Sekunden lang eine Tiefe Wunde und +10..50 Punkte Schaden zufügt.','en'=>'Your animal companion attempts an Enraged Lunge that applies a Deep Wound to target foe for 5..20 seconds and deals +10..50 damage.'],'concise'=>['de'=>'','en'=>'Inflicts Deep Wound condition (5...20 seconds) and deals +10...50 damage.']],'pvp'=>['type'=>11,'upkeep'=>0,'energy'=>5,'activation'=>0,'recharge'=>5,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Erzürnter Ausfall (PvP)','en'=>'Enraged Lunge (PvP)'],'desc'=>['de'=>'Euer Tiergefährte greift mit einem Erzürnten Ausfall an, der für jede Tierbeherrschungs-Fertigkeit, die wieder aufgeladen wird, +3..23 Punkte Schaden (maximaler Bonus 80) zufügt.','en'=>'Your animal companion attempts an Enraged Lunge that deals +3..23 damage (maximum bonus 80) for each recharging Beast Mastery skill.'],'concise'=>['de'=>'','en'=>'Deals +3...23 damage (maximum 80) for each of your recharging Beast Mastery skills.']]],
