1206=>['campaign'=>2,'profession'=>2,'attribute'=>22,'elite'=>false,'split'=>false,'pve'=>['type'=>11,'upkeep'=>0,'energy'=>5,'activation'=>0,'recharge'=>20,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Sprung','en'=>'Pounce'],'desc'=>['de'=>'Der nächste Angriff Eures Tiergefährten ist ein Sprung, der +5..20 Punkte Schaden zufügt. Trifft dieser Angriff einen Gegner, der sich bewegt, wird dieser zu Boden geworfen.','en'=>'Your animal companion\'s next attack is a Pounce that deals +5..20 damage. If the attack strikes a moving foe, that foe is knocked down.'],'concise'=>['de'=>'','en'=>'Deals +5...20 damage. Causes knock-down if target foe is moving.']],'pvp'=>null],
1209=>['campaign'=>2,'profession'=>2,'attribute'=>22,'elite'=>false,'split'=>false,'pve'=>['type'=>29,'upkeep'=>0,'energy'=>10,'activation'=>0,'recharge'=>10,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Bestienwut','en'=>'Bestial Fury'],'desc'=>['de'=>'Alle Eure Nicht-Angriffsfertigkeiten werden 5 Sekunden lang deaktiviert. 5..11 Sekunden lang greift Ihr um 25% schneller an.','en'=>' All your non-attack skills are disabled for 5 seconds. For 5..11 seconds, you attack 25% faster.'],'concise'=>['de'=>'','en'=>'(5...11 seconds.) You attack 25% faster. <i>Your non-attack skills are disabled (5 seconds).</i>']],'pvp'=>null],
1211=>['campaign'=>2,'profession'=>2,'attribute'=>22,'elite'=>false,'split'=>false,'pve'=>['type'=>30,'upkeep'=>0,'energy'=>10,'activation'=>2,'recharge'=>20,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Vipernnest','en'=>'Viper\'s Nest'],'desc'=>['de'=>'Erzeugt ein Vipernnest. Wenn es ausgelöst wird, erleiden alle getroffenen Gegner in der Nähe 5..35 Punkte Stichschaden und werden 5..20 Sekunden lang vergiftet. Vipernnest endet nach 90 Sekunden. Diese Falle kann leicht unterbrochen werden.','en'=>'Create a Viper\'s Nest. When it is triggered, all nearby foes are struck for 5..35 piercing damage and become Poisoned for 5..20 seconds. Viper\'s Nest expires after 90 seconds. This Trap is easily interrupted.'],'concise'=>['de'=>'','en'=>'(90 seconds.) Affects nearby foes. Deals 5...35 piercing damage. Inflicts Poisoned condition (5...20 seconds). <i>Easily interrupted.</i>']],'pvp'=>null],
1212=>['campaign'=>2,'profession'=>2,'attribute'=>24,'elite'=>true,'split'=>false,'pve'=>['type'=>19,'upkeep'=>0,'energy'=>10,'activation'=>3,'recharge'=>15,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Tagundnachtgleiche','en'=>'Equinox'],'desc'=>['de'=>'Erstellt einen Geist der Stufe 1..10. In seiner Reichweite gewirkte Zauber, die Überzaubern hervorrufen, verursachen zusätzlich 10 Überzaubern. Dieser Geist stirbt nach 30..150 Sekunden.','en'=>'Create a level 1..10 spirit. Spells cast within its range that cause Overcast cause an additional 10 Overcast. This spirit dies after 30..150 seconds.'],'concise'=>['de'=>'','en'=>'Creates a level 1...10 spirit (30...150 second lifespan). Overcast-causing spells cast within range cause an additional 10 Overcast.']],'pvp'=>null],
1213=>['campaign'=>2,'profession'=>2,'attribute'=>24,'elite'=>false,'split'=>false,'pve'=>['type'=>19,'upkeep'=>0,'energy'=>15,'activation'=>5,'recharge'=>60,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Ruhe','en'=>'Tranquility'],'desc'=>['de'=>'Erstellt einen Geist der Stufe 1..10. Von Kreaturen in seiner Reichweite, die keine Geister sind, gewirkte Verzauberungen enden 20..50% schneller . Dieser Geist stirbt nach 15..60 Sekunden.','en'=>'Create a level 1..10 spirit. Enchantments cast by non-spirit creatures within its range expire 20..50% faster.  This spirit dies after 15..60 seconds.'],'concise'=>['de'=>'','en'=>'Creates a level 1...10 spirit (15...60 second lifespan). Enchantments expire 20...50% faster on creatures in range. Does not affect spirits.']],'pvp'=>null],
1215=>['campaign'=>2,'profession'=>8,'attribute'=>34,'elite'=>true,'split'=>false,'pve'=>['type'=>22,'upkeep'=>0,'energy'=>10,'activation'=>1,'recharge'=>8,'adrenaline'=>0,'sacrifice'=>0,'overcast'=>0,'name'=>['de'=>'Gezeter der Seelen','en'=>'Clamor of Souls'],'desc'=>['de'=>'Der Gegner und alle Feinde in der Nähe erleiden 10..65 Punkte Blitzschaden. Wenn Ihr Euch in Hörweite eines Geistes befindet oder einen gebündelten Gegenstand haltet, erhaltet Ihr 10 Punkte Energie.','en'=>'Target foe and all nearby foes take 10..65 lightning damage. If you are within earshot of a spirit or holding a bundle item, you gain 10 Energy.'],'concise'=>['de'=>'','en'=>'Deals 10...65 lightning damage to target and nearby foes. You gain 10 Energy if you are within earshot of a spirit or holding a bundle item.']],'pvp'=>null],
